/*+big_query=true*/ select c_custkey, c_name, sum(l_extendedprice) as revenue, sum(1 - l_discount) as average, c_acctbal, n_name, c_address, c_phone from customer, orders, lineitem, nation where c_custkey = o_custkey and l_orderkey = o_orderkey and o_orderdate >= date'1993-10-01' and o_orderdate < date '1994-1-01' and l_returnflag = 'R' and c_nationkey = n_nationkey group by c_custkey, c_name, c_acctbal, c_phone, n_name, c_address, c_comment order by c_custkey desc, c_name desc, revenue desc limit 20;